import React, { useEffect, useState } from "react";
import { useRouter } from "next/router";
import Splash from "../components/Splash";

const Page = () => {
  const router = useRouter();
  [isLoading, setLoading] = useState(true);
  useEffect(() => {
    setLoading(false);
  });
  return { isLoading };
};

export default Page;
