import React, { useState } from "react";

import Link from "next/link";

const NavLink = ({ id }) => {
  const [isActive, setActive] = useState(false);
  const handleToggle = () => {
    setActive(true);
  };
  const link = document.querySelector("#paintings");
  console.log(link);
  return (
    <li onClick={handleToggle}>
      <Link href="/[id]" as={`/${id}`}>
        <a className={isActive ? "active" : undefined}>{id}</a>
      </Link>
    </li>
  );
};

export default NavLink;
