import { useState } from "react";

export default initialVal => {
  const [value, setValue] = useState(initialVal);
  const handleChange = e => setValue(e.target.value);
  const handleSubmit = e => {
    // e.preventDefault();
    setValue(" ");
  };
  return [value, handleChange, handleSubmit];
};
