import React, { useState } from "react";

const Form = () => {
  const [input, setInput] = useState({
    email: ""
  });
  const handleChange = e =>
    setInput({ ...input, [e.target.title]: e.target.value });
  return (
    <div>
      <form>
        <input
          type="email"
          title="email"
          placeholder="enter your email"
          onChange={handleChange}
        />
      </form>
      <button>submit</button>
    </div>
  );
};
export default Form;
