import React, { useState } from "react";
import Form from "../Form";

const Footer = () => {
  const [isForm, setForm] = useState(true);
  const handleFormState = () => {
    setForm(false);
  };
  return (
    <div>
      {isForm ? (
        <Form toggleForm={handleFormState} />
      ) : (
        <p>Thank you for signing up!</p>
      )}
    </div>
  );
};

export default Footer;
