import React from "react";
import useInputState from "../utilities/hooks/useInputState";

const Form = ({ handleFormState }) => {
  const [value, handleChange, handleSubmit] = useInputState(" ");
  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        title="email"
        placeholder="enter your email"
        onChange={handleChange}
        value={value}
      />
      <button onClick={handleFormState()}>Submit</button>
    </form>
  );
};
export default Form;
